mapper.insert:
INSERT INTO t_dm_user
SET idm = #{idm},
iuser = #{iuser}

mapper.select:
SELECT A.idm, A.last_msg AS lastMsg, A.last_msg_at AS lastMsgAt, B.nm AS otherPersonNm, B.pic AS otherPersonPic
FROM t_dm A INNER JOIN t_dm_user B ON A.idm = B.idm INNER JOIN t_dm_user C ON B.idm = C.idm INNER JOIN t_user D ON C.iuser = D.iuser
WHERE B.iuser = #{loginedIuser} AND C.iuser != #{loginedIuser} ORDER BY A.last_msg_at DESC LIMIT #{startIdx}, #{rowCount}

mapper.insert:

    SELECT IFNULL(MAX(seq), 0) + 1
    FROM t_dm_msg
    WHERE idm = #{idm}

INSERT INTO t_dm_msg
SET idm = #{idm}, seq = #{seq}, iuser = #{loginedIuser}, msg = #{msg}
